import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useEvents } from '../../context/EventContext';
import './EventList.css';

const EventList = () => {
    const { events, loading, error, registerForEvent, unregisterFromEvent } = useEvents();
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');
    const [selectedStatus, setSelectedStatus] = useState('');

    const handleRegister = async (eventId) => {
        try {
            await registerForEvent(eventId);
        } catch (error) {
            alert(error.response?.data?.message || 'Failed to register for event');
        }
    };

    const handleUnregister = async (eventId) => {
        try {
            await unregisterFromEvent(eventId);
        } catch (error) {
            alert(error.response?.data?.message || 'Failed to unregister from event');
        }
    };

    const filteredEvents = events.filter(event => {
        const matchesSearch = event.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            event.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            event.location.toLowerCase().includes(searchTerm.toLowerCase());

        const matchesCategory = selectedCategory ? event.category === selectedCategory : true;
        const matchesStatus = selectedStatus ? event.status === selectedStatus : true;

        return matchesSearch && matchesCategory && matchesStatus;
    });

    if (loading) {
        return <div className="loading">Loading events...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="event-list-container">
            <div className="filters">
                <input
                    type="text"
                    placeholder="Search events..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                />

                <select
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    className="filter-select"
                >
                    <option value="">All Categories</option>
                    <option value="Conference">Conference</option>
                    <option value="Workshop">Workshop</option>
                    <option value="Seminar">Seminar</option>
                    <option value="Social">Social</option>
                    <option value="Other">Other</option>
                </select>

                <select
                    value={selectedStatus}
                    onChange={(e) => setSelectedStatus(e.target.value)}
                    className="filter-select"
                >
                    <option value="">All Status</option>
                    <option value="upcoming">Upcoming</option>
                    <option value="ongoing">Ongoing</option>
                    <option value="completed">Completed</option>
                    <option value="cancelled">Cancelled</option>
                </select>
            </div>

            <div className="events-grid">
                {filteredEvents.length === 0 ? (
                    <div className="no-events">No events found</div>
                ) : (
                    filteredEvents.map(event => (
                        <div key={event._id} className="event-card">
                            <h3>{event.name}</h3>
                            <p className="event-description">{event.description}</p>
                            <div className="event-details">
                                <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
                                <p><strong>Time:</strong> {event.time}</p>
                                <p><strong>Location:</strong> {event.location}</p>
                                <p><strong>Category:</strong> {event.category}</p>
                                <p><strong>Status:</strong> {event.status}</p>
                                <p className={`capacity ${event.registeredParticipants >= event.capacity ? 'full' : ''}`}>
                                    <strong>Capacity:</strong> {event.registeredParticipants}/{event.capacity}
                                </p>
                            </div>
                            <div className="event-actions">
                                <Link to={`/event/${event._id}`} className="view-details-btn">
                                    View Details
                                </Link>
                                {event.registeredParticipants < event.capacity ? (
                                    <button 
                                        onClick={() => handleRegister(event._id)}
                                        className="register-btn"
                                    >
                                        Register
                                    </button>
                                ) : (
                                    <button 
                                        onClick={() => handleUnregister(event._id)}
                                        className="unregister-btn"
                                    >
                                        Unregister
                                    </button>
                                )}
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default EventList;
