import React, { useState } from 'react';
import './EventDetails.css';

const EventDetails = ({ eventId }) => {
    const [event] = useState({
        id: eventId,
        name: 'Tech Conference 2024',
        date: '2024-03-15',
        location: 'Convention Center',
        description: 'Join us for the biggest tech conference of the year! Featured speakers, workshops, and networking opportunities.',
        organizer: 'Tech Events Inc.',
        capacity: 200,
        registeredParticipants: 120,
        agenda: [
            { time: '09:00 AM', activity: 'Registration' },
            { time: '10:00 AM', activity: 'Keynote Speech' },
            { time: '11:30 AM', activity: 'Workshop Session 1' },
            { time: '01:00 PM', activity: 'Lunch Break' },
            { time: '02:00 PM', activity: 'Workshop Session 2' },
            { time: '04:00 PM', activity: 'Networking Event' }
        ]
    });

    const [isRegistered, setIsRegistered] = useState(false);

    const handleRegister = () => {
        setIsRegistered(true);
        // Here you would typically make an API call to register the user
    };

    return (
        <div className="event-details-container">
            <div className="event-header">
                <h1>{event.name}</h1>
                <div className="event-meta">
                    <span className="date">{event.date}</span>
                    <span className="location">{event.location}</span>
                </div>
            </div>

            <div className="event-body">
                <div className="main-info">
                    <section className="description">
                        <h2>About the Event</h2>
                        <p>{event.description}</p>
                    </section>

                    <section className="organizer">
                        <h2>Organizer</h2>
                        <p>{event.organizer}</p>
                    </section>

                    <section className="capacity">
                        <h2>Capacity</h2>
                        <div className="capacity-bar">
                            <div 
                                className="capacity-fill"
                                style={{ width: `${(event.registeredParticipants / event.capacity) * 100}%` }}
                            ></div>
                        </div>
                        <p>{event.registeredParticipants} / {event.capacity} spots filled</p>
                    </section>

                    <section className="agenda">
                        <h2>Event Agenda</h2>
                        <div className="timeline">
                            {event.agenda.map((item, index) => (
                                <div key={index} className="timeline-item">
                                    <div className="time">{item.time}</div>
                                    <div className="activity">{item.activity}</div>
                                </div>
                            ))}
                        </div>
                    </section>
                </div>

                <div className="registration-panel">
                    {!isRegistered ? (
                        <button 
                            className="register-btn"
                            onClick={handleRegister}
                            disabled={event.registeredParticipants >= event.capacity}
                        >
                            {event.registeredParticipants >= event.capacity 
                                ? 'Event Full' 
                                : 'Register Now'}
                        </button>
                    ) : (
                        <div className="registered-message">
                            You're registered for this event!
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventDetails;
