import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useEvents } from '../../context/EventContext';
import './EventDetails.css';

const EventDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { events, registerForEvent } = useEvents();

    const event = events.find(e => e.id === parseInt(id));

    if (!event) {
        return (
            <div className="event-details-container">
                <div className="error-message">
                    <h2>Event Not Found</h2>
                    <p>The event you're looking for doesn't exist.</p>
                    <button onClick={() => navigate('/events')} className="back-btn">
                        Back to Events
                    </button>
                </div>
            </div>
        );
    }

    const handleRegister = () => {
        if (event.registeredParticipants >= event.capacity) {
            alert('Sorry, this event is already full!');
            return;
        }
        registerForEvent(event.id);
        alert('Successfully registered for the event!');
    };

    const isEventFull = event.registeredParticipants >= event.capacity;
    const isUpcoming = new Date(event.date) >= new Date();

    return (
        <div className="event-details-container">
            <button onClick={() => navigate('/events')} className="back-btn">
                ← Back to Events
            </button>

            <div className="event-header">
                <h1>{event.name}</h1>
                <div className="event-meta">
                    <span className="date"> {new Date(event.date).toLocaleDateString()}</span>
                    <span className="location"> {event.location}</span>
                </div>
            </div>

            <div className="event-body">
                <div className="main-info">
                    <section className="description">
                        <h2>About the Event</h2>
                        <p>{event.description}</p>
                    </section>

                    <section className="capacity">
                        <h2>Capacity</h2>
                        <div className="capacity-bar-container">
                            <div 
                                className="capacity-fill"
                                style={{ 
                                    width: `${(event.registeredParticipants / event.capacity) * 100}%`,
                                    backgroundColor: isEventFull ? '#dc3545' : '#28a745'
                                }}
                            />
                        </div>
                        <p>{event.registeredParticipants} / {event.capacity} spots filled</p>
                    </section>
                </div>

                <div className="registration-panel">
                    {!isUpcoming ? (
                        <div className="past-event-message">
                            This event has already taken place
                        </div>
                    ) : isEventFull ? (
                        <div className="full-event-message">
                            This event is full
                        </div>
                    ) : (
                        <button 
                            onClick={handleRegister}
                            className="register-btn"
                        >
                            Register Now
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default EventDetails;
