import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useEvents } from '../../context/EventContext';
import { useAuth } from '../../context/AuthContext';
import './EventDetails.css';

const EventDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { events, loading, error, registerForEvent, unregisterFromEvent } = useEvents();
    const { currentUser } = useAuth();
    const [event, setEvent] = useState(null);
    const [registrationLoading, setRegistrationLoading] = useState(false);
    const [registrationError, setRegistrationError] = useState(null);

    useEffect(() => {
        const currentEvent = events.find(e => e._id === id);
        if (currentEvent) {
            setEvent(currentEvent);
        }
    }, [id, events]);

    const handleRegister = async () => {
        setRegistrationLoading(true);
        setRegistrationError(null);
        try {
            await registerForEvent(id);
        } catch (error) {
            setRegistrationError(error.response?.data?.message || 'Failed to register for event');
        } finally {
            setRegistrationLoading(false);
        }
    };

    const handleUnregister = async () => {
        setRegistrationLoading(true);
        setRegistrationError(null);
        try {
            await unregisterFromEvent(id);
        } catch (error) {
            setRegistrationError(error.response?.data?.message || 'Failed to unregister from event');
        } finally {
            setRegistrationLoading(false);
        }
    };

    if (loading) return <div className="loading">Loading...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!event) return <div className="error">Event not found</div>;

    const isRegistered = event.registeredParticipants.some(
        participant => participant.user._id === currentUser?.id
    );

    const isCreator = event.createdBy?._id === currentUser?.id;
    const isFull = event.registeredParticipants.length >= event.capacity;

    return (
        <div className="event-details">
            <h1>{event.name}</h1>

            <div className="event-info">
                <p className="description">{event.description}</p>

                <div className="details-grid">
                    <div className="detail-item">
                        <span className="label">Date:</span>
                        <span>{new Date(event.date).toLocaleDateString()}</span>
                    </div>

                    <div className="detail-item">
                        <span className="label">Time:</span>
                        <span>{event.time}</span>
                    </div>

                    <div className="detail-item">
                        <span className="label">Location:</span>
                        <span>{event.location}</span>
                    </div>

                    <div className="detail-item">
                        <span className="label">Category:</span>
                        <span>{event.category}</span>
                    </div>

                    <div className="detail-item">
                        <span className="label">Capacity:</span>
                        <span>{event.registeredParticipants.length} / {event.capacity}</span>
                    </div>

                    <div className="detail-item">
                        <span className="label">Status:</span>
                        <span className={`status ${event.status}`}>{event.status}</span>
                    </div>
                </div>

                {registrationError && (
                    <div className="error-message">{registrationError}</div>
                )}

                {!isCreator && (
                    <div className="registration-section">
                        {isRegistered ? (
                            <button
                                onClick={handleUnregister}
                                disabled={registrationLoading}
                                className="unregister-button"
                            >
                                {registrationLoading ? 'Processing...' : 'Cancel Registration'}
                            </button>
                        ) : (
                            <button
                                onClick={handleRegister}
                                disabled={registrationLoading || isFull}
                                className={`register-button ${isFull ? 'disabled' : ''}`}
                            >
                                {registrationLoading ? 'Processing...' : isFull ? 'Event Full' : 'Register'}
                            </button>
                        )}
                    </div>
                )}
            </div>

            {event.registeredParticipants.length > 0 && (
                <div className="participants-section">
                    <h2>Registered Participants</h2>
                    <div className="participants-list">
                        {event.registeredParticipants.map(participant => (
                            <div key={participant.user._id} className="participant-item">
                                <span className="participant-name">{participant.user.name}</span>
                                <span className="registration-date">
                                    Registered on: {new Date(participant.registeredAt).toLocaleDateString()}
                                </span>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            <button onClick={() => navigate('/events')} className="back-button">
                Back to Events
            </button>
        </div>
    );
};

export default EventDetails;
