import React, { Component } from 'react';
import { useNavigate } from 'react-router-dom';
import { useEvents } from '../../context/EventContext';
import './EventCreate.css';

// Wrapper function to use hooks with class component
function WithNavigate(Component) {
    return props => <Component {...props} navigate={useNavigate()} useEvents={useEvents()} />;
}

class EventCreate extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            description: '',
            date: '',
            time: '',
            location: '',
            capacity: '',
            category: 'Conference',
            error: null,
            loading: false
        };
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };

    handleSubmit = async (e) => {
        e.preventDefault();
        const { name, description, date, time, location, capacity, category } = this.state;
        const { createEvent } = this.props.useEvents;
        const { navigate } = this.props;

        try {
            this.setState({ loading: true, error: null });

            const eventData = {
                name,
                description,
                date,
                time,
                location,
                capacity: parseInt(capacity),
                category,
                registeredParticipants: 0
            };

            await createEvent(eventData);
            navigate('/events');
        } catch (error) {
            this.setState({ 
                error: error.response?.data?.message || 'Error creating event'
            });
        } finally {
            this.setState({ loading: false });
        }
    };

    render() {
        const { name, description, date, time, location, capacity, category, error, loading } = this.state;

        return (
            <div className="event-create-container">
                <h2>Create New Event</h2>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={this.handleSubmit} className="event-form">
                    <div className="form-group">
                        <label>Event Name:</label>
                        <input
                            type="text"
                            name="name"
                            value={name}
                            onChange={this.handleChange}
                            required
                            placeholder="Enter event name"
                        />
                    </div>

                    <div className="form-group">
                        <label>Description:</label>
                        <textarea
                            name="description"
                            value={description}
                            onChange={this.handleChange}
                            required
                            placeholder="Enter event description"
                        />
                    </div>

                    <div className="form-group">
                        <label>Date:</label>
                        <input
                            type="date"
                            name="date"
                            value={date}
                            onChange={this.handleChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label>Time:</label>
                        <input
                            type="time"
                            name="time"
                            value={time}
                            onChange={this.handleChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label>Location:</label>
                        <input
                            type="text"
                            name="location"
                            value={location}
                            onChange={this.handleChange}
                            required
                            placeholder="Enter event location"
                        />
                    </div>

                    <div className="form-group">
                        <label>Capacity:</label>
                        <input
                            type="number"
                            name="capacity"
                            value={capacity}
                            onChange={this.handleChange}
                            required
                            min="1"
                            placeholder="Enter maximum capacity"
                        />
                    </div>

                    <div className="form-group">
                        <label>Category:</label>
                        <select
                            name="category"
                            value={category}
                            onChange={this.handleChange}
                            required
                        >
                            <option value="Conference">Conference</option>
                            <option value="Workshop">Workshop</option>
                            <option value="Seminar">Seminar</option>
                            <option value="Social">Social</option>
                            <option value="Other">Other</option>
                        </select>
                    </div>

                    <button type="submit" disabled={loading}>
                        {loading ? 'Creating...' : 'Create Event'}
                    </button>
                </form>
            </div>
        );
    }
}

export default WithNavigate(EventCreate);
