import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useEvents } from '../../context/EventContext';
import './EventList.css';

const EventList = () => {
    const { events } = useEvents();
    const navigate = useNavigate();
    const [searchTerm, setSearchTerm] = useState('');
    const [filterType, setFilterType] = useState('all');

    const filteredEvents = events.filter(event => {
        const matchesSearch = event.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            event.location.toLowerCase().includes(searchTerm.toLowerCase());

        if (filterType === 'all') return matchesSearch;
        if (filterType === 'upcoming') {
            return matchesSearch && new Date(event.date) >= new Date();
        }
        return matchesSearch && new Date(event.date) < new Date();
    });

    const handleEventClick = (eventId) => {
        navigate(`/event/${eventId}`);
    };

    return (
        <div className="event-list-container">
            <div className="filters">
                <input
                    type="text"
                    placeholder="Search events..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                />
                <select
                    value={filterType}
                    onChange={(e) => setFilterType(e.target.value)}
                    className="filter-select"
                >
                    <option value="all">All Events</option>
                    <option value="upcoming">Upcoming Events</option>
                    <option value="past">Past Events</option>
                </select>
            </div>

            {filteredEvents.length === 0 ? (
                <div className="no-events">
                    <h3>No events found</h3>
                    <p>Try adjusting your search or create a new event!</p>
                </div>
            ) : (
                <div className="events-grid">
                    {filteredEvents.map(event => (
                        <div 
                            key={event.id} 
                            className="event-item"
                            onClick={() => handleEventClick(event.id)}
                        >
                            <h3>{event.name}</h3>
                            <div className="event-details">
                                <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
                                <p><strong>Location:</strong> {event.location}</p>
                                <p><strong>Participants:</strong> {event.registeredParticipants} / {event.capacity}</p>
                                <p className="description"><strong>Description:</strong> {event.description}</p>
                            </div>
                            <div className="capacity-indicator">
                                <div 
                                    className="capacity-bar" 
                                    style={{ 
                                        width: `${(event.registeredParticipants / event.capacity) * 100}%`,
                                        backgroundColor: event.registeredParticipants >= event.capacity ? '#dc3545' : '#28a745'
                                    }}
                                />
                            </div>
                            <button 
                                className="view-details-btn"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    handleEventClick(event.id);
                                }}
                            >
                                View Details
                            </button>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default EventList;
